CREATE TABLE IF NOT EXISTS news ( id int(10) unsigned NOT NULL auto_increment, date_news datetime default NULL, head varchar(255) default NULL, body text, id_page int(10) unsigned default NULL, is_hidden char(1) default 'n', full_text text, top_index int(11) NOT NULL default '0', sortir int(11) default NULL, spec_news int(10) unsigned default '0', id_group int(10) unsigned default '0', PRIMARY KEY  (id), KEY date_news (date_news) )